import { GameService } from 'src/app/services/game.service';
import { Observable, Subject, debounceTime, distinctUntilChanged, switchMap } from 'rxjs';
import { Component, OnInit } from '@angular/core';
import { Games } from 'src/app/model/Games'
import { Router } from '@angular/router';

@Component({
  selector: 'app-games',
  templateUrl: './games.component.html',
  styleUrls: ['./games.component.css']
})
export class GamesComponent implements OnInit{
  constructor(private gameService:GameService){}

games!: Observable<Games[]>;
gamess: Games[];

private searchGame= new Subject<string>();

ngOnInit(): void {
  this.games = this.searchGame.pipe(
    switchMap((term: string) => this.gameService.searchGame(term).subscribe()(games => {this.gamess = games;})) 
  );

}

}
