import { GameService } from 'src/app/services/game.service';
import { Observable, Subject, debounceTime, distinctUntilChanged, switchMap } from 'rxjs';
import { Component } from '@angular/core';
import { Games } from 'src/app/model/Games'
import { Router } from '@angular/router';

import { MatDialog } from '@angular/material/dialog';



@Component({
  selector: 'app-games',
  templateUrl: './games.component.html',
  styleUrls: ['./games.component.css']
})



export class GamesComponent  {

constructor(public dialog: MatDialog,
private gameService:GameService){}

games!: Games[];


private searchGame= new Subject<string>();



searchByTitle(title:string): void {

  this.gameService.searchGame(title).subscribe(returnedGames => {
    this.games = returnedGames;
    for(var game of this.games){
      console.log(game.title);
    }
  });

  }
  openDialog() {
    const dialogRef = this.dialog.open(GameDescription);

    dialogRef.afterClosed().subscribe(result => {
      console.log(`Dialog result: ${result}`);
    });
  }


}
